function getTagById(tagId, tags) {
    for (var i = 0; i < tags.length; i++) {
        if (tags[i].id == tagId) {
            return tags[i];
        }
        for (var j = 0; j < tags[i].children.length; j++) {
            if (tags[i].children[j].id == tagId) {
                return tags[i].children[j];
            }
        }
    }
    return undefined;
}
