function judgeIndexOfUser(user, users){
    for(var i = 0; i < users.length; i++){
        var id1 = (user.uId != undefined ? user.uId : user.uid);
        var id2 = (users[i].uId != undefined ? users[i].uId : users[i].uid);
        if(id1 == id2){
            return true;
        }
    }
    return false;
}

