//id
function filterUserById(users, id){
    return filterElemByProperty(users, 'uId', id);
}

//
function filterElemByProperty(array, propertyName, propertyValue){
    //var sss = new Array();
    for(var i = 0; i < array.length; i++){
        var elem = array[i];
        if(elem[propertyName] == propertyValue){
            return elem;
        }
    }
}

